function _multiTab(e) {
  let obj = {};
  multiTab.config.label.filter(label => {
    e.filter(item => {
      let check = item.label.some(s => s == label);
      if (check) {
        if (obj[label]) {
          obj[label].push(item)
        } else {
          obj[label] = [item];
        }
      }
    })
  })
  window.matchMedia("(max-width: 600px)").matches && (multiTab.config.maxTab = 3);
  window.matchMedia("(max-width: 480px)").matches && (multiTab.config.maxTab = 2);
  let html = '<div class="series-gen relative loading">';
  let nav = '<h2 class="nav-tabs tabs tab hidden lg:grid grid-cols-4 gap-4 max-w-screen-2xl mx-auto">';
  let inner = '<div class="listupd grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 max-w-screen-2xl mx-auto">';
  let post = '';
  let label = multiTab.shuffle(Object.keys(obj)).slice(0, multiTab.config.maxTab).sort().reduce((t, e) => (t[e] = obj[e], t), {});
  for (const key in label) {
    if (Object.hasOwnProperty.call(label, key)) {
      const item = multiTab.shuffle(label[key]).slice(0, multiTab.config.jumlah);
      nav += `<div class="cursor-pointer px-4 text-accent text-xl font-semibold"><span data-id="series-${key.replace(/\W/g, '').toLowerCase()}">${key}</span></div>`;
      post += `<div id="series-${key.replace(/\W/g, '').toLowerCase()}" class="customAF RSf6 scroll"><div class="scrolling inner divide-y divide-neutral-200 dark:divide-neutral-700 gap-x-4 gap-y-1 grid">`

      item.forEach(i => {
        let title = i.title;
        let score = i.label.find(i => !/[^0-9\.-]+/g.test(i)) || i.label.find(i => i.includes('Score'))?.split(' ')[1] || '0.0';
        let link = i.link;
        let status = i.label.find(i => ['Ongoing', 'Completed', 'Dropped', 'Cooming Soon'].includes(i)) || "";
        let tipe = i.label.find(i => ['Manga', 'Manhwa', 'Manhua', 'Novel', 'Doujin', 'LN', 'WN'].includes(i)) || "";
        let image = i.image;
		let doujin = i.label.find(i => ['Doujin'].includes(i)) || "";
		let lock = i.label.find(i => ['Lock'].includes(i)) || "";

        post += `<article class="customAgata px-4 py-4 ${lock}"><a class="grid items-center tempt grid-cols-[auto_50px_1fr] gap-x-2" href="${link}" title="${title}"><div class="number text-lg M1uq"></div>`;
        post += `<div class="images">`;
        post += `<img class="rounded" src="${image}" alt="${title}"/>`
        tipe && (post += `<span class="absolute po ${tipe.toLowerCase()}">${tipe}</span><div class="${lock} absolute inset-0 group-hover:bottom-0 w-full transition-all bg-[rgba(0,0,0,.48)] backdrop-saturate-150 backdrop-blur-sm text-white text-sm grid">
<span class="block p-2 text-center mt-[-8px] text-xs text-[#54ff94] grid self-center justify-items-center"><svg height="24" viewBox="0 0 24 24" width="24" xmlns="http://www.w3.org/2000/svg"><path d="M6 22q-.825 0-1.412-.587Q4 20.825 4 20V10q0-.825.588-1.413Q5.175 8 6 8h1V6q0-2.075 1.463-3.538Q9.925 1 12 1t3.538 1.462Q17 3.925 17 6v2h1q.825 0 1.413.587Q20 9.175 20 10v10q0 .825-.587 1.413Q18.825 22 18 22Zm0-2h12V10H6v10Zm6-3q.825 0 1.413-.587Q14 15.825 14 15q0-.825-.587-1.413Q12.825 13 12 13q-.825 0-1.412.587Q10 14.175 10 15q0 .825.588 1.413Q11.175 17 12 17ZM9 8h6V6q0-1.25-.875-2.125T12 3q-1.25 0-2.125.875T9 6ZM6 20V10v10Z" fill="currentColor"></path></svg>This items locked by Admin</span>
</div>`);
		doujin && (post += `<span class="absolute po nsfw" style="background-image: url(${image})"></span>`);
        post += `</div>`;
        post += `<h2 class="entry-title line-clamp-2 h-min font-medium"></span>${title}</h2>`;
        status && (post += `<div class="rating flex items-center overflow-hidden rounded gap-0.5 bg-neutral-700 justify-self-start text-xs"><span class="px-1" data-bg="${status}">${status}</span><div class="rating-prc px-1 bg-[#e3b5cd] text-neutral-900"><div class="rtp"><div class="rtb">`);
        post += `<span style="width:${score.length == 4 ? score == '10.0' ? score.replace('.', '') : score.replace('.', '').slice(0, -1) : score.replace('.', '')}%"></span>`;
        post += `</div></div><div class="flex items-center" content="${score}"><svg aria-hidden="true" class="w-4 h-4" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg"><title>First star</title><path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z"></path></svg>${score}</div></div></div>`
        post += `</a></article>`;
      })

      post += '</div></div>';
    }
  }
  html += `${nav}</h2>${inner + post}</div></div>`;
  document.getElementById('mTP_Slider').innerHTML = html;
  let se = document.querySelectorAll('[data-id*=series');
  se.forEach(i => {
    i.parentElement.addEventListener('click', e => {
      let id = e.currentTarget.querySelector('span').dataset.id;
      se.forEach(l => l.parentElement.classList.remove('active'));
      e.currentTarget.classList.add('active');
      document.querySelectorAll('.customAF.scroll').forEach(l => l.classList.remove('active'));
      document.getElementById(id).classList.add('active');
    })
  })
  se[0].click();
};

