function loadCDNs(scripts, callback) {
    if (!window.loadedScripts) {
      window.loadedScripts = {};
    }
    let loadedCount = 0;
    function loadScript(src, singleLoad, onload) {
      if (singleLoad && window.loadedScripts[src]) {
        onload();
        return;
      }
      const script = document.createElement('script');
      script.src = src;
      script.onload = function () {
        if (singleLoad) {
          window.loadedScripts[src] = true;  }
        onload();
      };
      script.onerror = function () {
        console.error('Failed to load script:', src);
      };
      document.head.appendChild(script);
    }

    for (const [src, singleLoad] of Object.entries(scripts)) {
      loadScript(src, singleLoad, function () {
        loadedCount++;
        if (loadedCount === Object.keys(scripts).length) {
          if (callback) callback();
        }
      });
    }
  }
  const cdnScripts = { 'https://cdnjs.cloudflare.com/ajax/libs/flowbite/2.1.1/flowbite.min.js': true,  'https://cdn.jsdelivr.net/gh/MagicReincarnation/codeHirutshuji@main/codebyhirutshuji_lite/logincode_litex_v4.0.js': true, 'https://cdn.jsdelivr.net/gh/MagicReincarnation/codeHirutshuji@main/codebyhirutshuji_lite/lisensiCodeLogin_lite.js': true,
  };
  loadCDNs(cdnScripts, function () {
    console.log('Semua script berhasil dimuat!');
  });
